SELECT competitorId, AVG(CASE WHEN criteria = 'form' THEN value ELSE 0 end) AS sumform, SUM(CASE WHEN criteria = 'difficulty' THEN value ELSE 0 end) AS sumdiff, SUM(CASE WHEN criteria = 'tof' THEN value ELSE 0 end) AS tof, (AVG(CASE WHEN criteria = 'form' THEN value ELSE 0 end) + SUM(CASE WHEN criteria = 'difficulty' THEN value ELSE 0 end) - SUM(CASE WHEN criteria = 'tof' THEN value ELSE 0 end)) AS score  FROM scores WHERE disciplineId LIKE 1 AND round LIkE 1 GROUP BY competitorId